<?php	class HTML{		public function __construct(){					}		public function link($href, $text){			return "<a href\"=" . $href . "\">" . $text . "</a>";		}		public function items($items){			return "<ul><li>" . implode('</li><li>', $items) . "</li></ul>";		}		public function image($src, $alt = ''){			return "<img src=" . $src . "alt=" . $alt . ">";		}		public function make_standart_form($PDO_user, $selected_id, $header = '', $common_friends = ''){			$friends_list = '';			if(!empty($common_friends)){				$st = $common_friends;			}			foreach($PDO_user as $t){				//Write them in a standart form				$select = array();				$select_id = array();				$select[$t['id']] = '';				$select[$selected_id] = '_selected';				$select_id[$t['id']] = $t['id'];				$select_id[$selected_id] = '_selected';								if(empty($st)){					$st = $t['status'];				}								$friends_list .= '<a href="?selected_id=' . $t['id'] . $header . '" class="found_anchor">' . /*  onclick="finder.show_user_information(' . $t['id'] . '); return false;" */									'<div class="found' . $select[$t['id']] . '" id="found' . $select_id[$t['id']] . '" name="' . $t['id'] . '">' .										'<img src="uploads/user_images/' . $t['default_image'] . '" class="found_img"/>' .										'<div class="found_inf' . $select[$t['id']] . '">' .											'<div class="found_name' . $select[$t['id']] . '" id="found_name' . $select_id[$t['id']] . '">' . $t['firstname'] . ' ' . $t['lastname'] . '</div>' .											'<div class="found_status' . $select[$t['id']] . '" id="found_status' . $select_id[$t['id']] . '">' . $st . '</div>' .										'</div>' .									'</div>' .								'</a>';			}			return $friends_list;		}	}?>