<?php 	class database{		public $config = array(			'DB_USERNAME' => 'root',			'DB_PASSWORD' => '',			'DB_NAME' => 'dbname='		);		public function __construct($dbname){			if((!empty($dbname)) && ($dbname != 'a')){					$this->config['DB_NAME'] .= $dbname;				}else{					$this->config['DB_NAME'] = '';				}			try{				$this->conn = new PDO('mysql:host=localhost;' . $this->config['DB_NAME'], 								$this->config['DB_USERNAME'], 								$this->config['DB_PASSWORD']);				$this->conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);				$this->conn->query('set charset utf8');				$this->conn->query("SET NAMES 'UTF8'");				$this->conn->query("SET collation_connection = 'UTF8_general_ci'");				$this->conn->query("SET collation_server = 'UTF8_general_ci'");				$this->conn->query("SET character_set_client = 'UTF8'");				$this->conn->query("SET character_set_connection = 'UTF8'");				$this->conn->query("SET character_set_results = 'UTF8'");				$this->conn->query("SET character_set_server = 'UTF8'");				return $this->conn;			}catch(PDOException $e){				echo 'ERROR: ' . $e->getMessage();				return false;			}		}				public function find($tablename, $columnname, $Ssearch1, $Ssearch2){			try {				$this->result = $this->conn->query('SELECT ' . $columnname . ' FROM ' . $tablename . ' WHERE ' . $columnname . '=' . '\'' . $Ssearch1 . '\'');				if ($this->result->rowCount()>0){ 						foreach($this->result as $this->res){						$this->m = $this->res['password'];					}					if($this->m == $Ssearch2) return true; else return false;				}else return false;			} catch (Exception $e) {				return false;			}		}		public function getrow($tablename, $columnname, $Ssearch){			try {				$this->result = $this->conn->query('SELECT * ' . ' FROM ' . $tablename . ' WHERE ' . $columnname . '=' . '\'' . $Ssearch . '\'');				if ($this->result->rowCount()>0){ 						return $this->result;				}else return false;			} catch ( Exception $e ){				return false;			}		}			}?>