<?phpclass file{		public function __construct(){					}		public function exists($file){			return file_exists($file);		}		public function size($file){			return filesize($file);		}		public function name($file){			return pathinfo($file, PATHINFO_FILENAME);		}		public function extension($file){			return pathinfo($file, PATHINFO_EXTENSION);		}		public function delete($file){			return unlink($file);		}	}?>